<?php/** * Plugin Name: Wepay Payment Gateway * Plugin URI: wepay.vn * Description: Công cụ thanh toán điện tử WePay - Nhanh chóng, Tiện lợi, An Toàn * Version: 1.1 * Author: chieund */if ( ! defined( 'ABSPATH' ) ) {	exit;} // Exit if accessed directlyif ( in_array( 'woocommerce/woocommerce.php', apply_filters( 'active_plugins', get_option( 'active_plugins' ) ) ) ) {	add_action( 'plugins_loaded', 'init_wepay_gateway_class' );	function init_wepay_gateway_class() {		include_once(plugin_basename('class-wc-gateway-wepay.php'));		class WC_Wepay extends WC_Gateway_Wepay {			public function __construct() {				_deprecated_function( 'WC_Wepay', '1.1', 'WC_Gateway_Wepay' );				parent::__construct();			}		}		//Defining class gateway		function add_gateway_class( $methods ) {			$methods[] = 'WC_Gateway_Wepay';			return $methods;		}		add_filter( 'woocommerce_payment_gateways', 'add_gateway_class' );	}}